<?php/** * Created by PhpStorm. * User: Administrator * Date: 19-3-5 * Time: 上午11:34 */namespace app\api\controller;use think\Controller;use think\Exception;ini_set('display_errors', 'on');error_reporting(E_ALL);class Api extends Controller{    public function getQrcode($url)    {        ini_set('display_errors', 'on');        error_reporting(E_ALL);        include_once "extend/phpqrcode/qrlib.php";        $Qrcode = new \QRcode();        $errorCorrectionLevel = 'L';        $matrixPointSize = 6;        Header("Content-type: image/png");        $Qrcode::png($url, false, $errorCorrectionLevel, $matrixPointSize, 2);    }    public function help_make_show($data)    {        echo '<meta http-equiv="Content-Type" content="textml; charset=utf-8" />';        echo '<meta http-equiv="Cache-Control" content="no-cache" />';        echo '<meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0" />';        echo '<meta name="apple-mobile-web-app-capable" content="yes" />';        echo '<meta name="apple-mobile-web-app-status-bar-style" content="black" />';        echo '<meta name="format-detection" content="telephone=yes" />';        echo '<style type="text/css">#title{text-align: center; padding-bottom: 10px;  font-size: 20px;}#times{color: #c3c3c3; text-align: left; margin-bottom: 25px;}  #divs{ word-wrap: break-word; word-break: normal;  } img{width: 100%} #classname{text-align: center; margin-bottom: 15px; font-size: 24px; font-weight: bold;}</style>';//        echo "<div id='title'>".htmlspecialchars_decode($data['title'])."</div>";        echo "<div id='divs'>" . htmlspecialchars_decode($data['content']) . "</div>";    }    public function help_make_show2($data)    {        echo '<meta http-equiv="Content-Type" content="textml; charset=utf-8" />';        echo '<meta http-equiv="Cache-Control" content="no-cache" />';        echo '<meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0" />';        echo '<meta name="apple-mobile-web-app-capable" content="yes" />';        echo '<meta name="apple-mobile-web-app-status-bar-style" content="black" />';        echo '<meta name="format-detection" content="telephone=yes" />';        echo '<style type="text/css">#title{text-align: center; padding-bottom: 10px;  font-size: 20px;}#times{color: #c3c3c3; text-align: left; margin-bottom: 25px;}  #divs{ word-wrap: break-word; word-break: normal;  } img{width: 100%} #classname{text-align: center; margin-bottom: 15px; font-size: 24px; font-weight: bold;}</style>';        echo "<div id='title'>" . htmlspecialchars_decode($data['title']) . "</div>";        echo "<div id='divs'>" . htmlspecialchars_decode($data['content']) . "</div>";    }}